
public class SwappingWithout3rd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10,b=20;
		
		System.out.println(" before swapping a= "+a+"  b= "+b);
		a=a+b; //30
		b=a-b; // 10
		a=a-b; //20
		System.out.println(" after swapping a= "+a+"  b= "+b);
		
	}

}
